import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function CharacterInfo({ match }) {
	useEffect(() => {
		fetchCharacterInfo();
		console.log(match);
	}, []);

	const [character, setCharacter] = useState([]);

	const fetchCharacterInfo = async () => {
		const fetchCharacterInfo = await fetch(
			`https://www.breakingbadapi.com/api/characters/${match.params.characterId}`
		);
		const character = await fetchCharacterInfo.json();
		setCharacter(character[0]);
		console.log(character);
	};

	return (
		<>
			<h3 className='page-title'>Character Info</h3>
            <img className='CharacterInfoImage' src={character.img} alt={character.name}/>
			<h4>{character.name}</h4>
			<ul>
				<li>character id: {character.char_id}</li>
				<li>alias: {character.nickname}</li>
				<li>birthday: {character.birthday}</li>
				<li>occupation: {character.occupation}</li>
				<li>played by: {character.portrayed}</li>
				<li>appears in seasons: {character.appearance}</li>
				<li>status: {character.status}</li>
			</ul>
		</>
	);
}

export default CharacterInfo;
