import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function EpisodesNav() {
	useEffect(() => {
		fetchEpisodes();
	}, []);

	const [episodes, setEpisodes] = useState([]);

	const fetchEpisodes = async () => {
		const episodesData = await fetch(
			'https://www.breakingbadapi.com/api/episodes'
		);
		const episodes = await episodesData.json();
		console.log(episodes);
		setEpisodes(episodes);
	};

	const seasonOneEpisode = (episodes.season === '1') ? true : null;

	return (
		<>
			<h3 className='page-title'>Episodes</h3>
			{episodes.map((seasonOneEpisode) => (
				<h5 key={seasonOneEpisode.episode_id}>
					<Link
						className='names-list'
						to={`/episodes/${seasonOneEpisode.episode_id}`}>
						{seasonOneEpisode.title}
					</Link>
				</h5>
			))}
		</>
	);
}

export default EpisodesNav;
