/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function CharacterInfo({ match }) {
	useEffect(() => {
		fetchCharacterInfo();
		console.log(match);
	}, []);

	const [character, setCharacter] = useState([]);

	const fetchCharacterInfo = async () => {
		const fetchCharacterInfo = await fetch(
			`https://www.breakingbadapi.com/api/characters/${match.params.characterId}`
		);
		const character = await fetchCharacterInfo.json();
		setCharacter(character[0]);
		console.log(character);
	};

	return (
		<section className='item-info'>
			<h3 className='page-title'>{character.name}</h3>
			<img
				className='character-image'
				src={character.img}
				alt={character.name}
			/>
			{/* <h4>{character.name}</h4> */}
			<div className='details-div'>
				<ul className='details'>
					<li className='details'>
						<span className='parameter'>character id:</span>
					</li>
					<li>
						<i>{character.char_id}</i>
					</li>
					<li className='details'>
						alias: <i>{character.nickname}</i>
					</li>
					<li className='details'>
						birthday: <i>{character.birthday}</i>
					</li>
					<li className='details'>
						occupation: <i>{character.occupation}</i>
					</li>
					<li className='details'>
						played by: <i>{character.portrayed}</i>
					</li>
					<li className='details'>
						appears in seasons: <i>{character.appearance}</i>
					</li>
					<li className='details'>
						status: <i>{character.status}</i>
					</li>
				</ul>
			</div>
			<Link className='back-to' to='/characters'>
				<h4>Back to Characters</h4>
			</Link>
		</section>
	);
}

export default CharacterInfo;
