import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function CharactersNav() {
	useEffect(() => {
		fetchCharacters();
	}, []);

	const [characters, setCharacters] = useState([]);

	const characterNames = {
		color: 'white'
	}

	const fetchCharacters = async () => {
		const characterData = await fetch(
			'https://www.breakingbadapi.com/api/characters'
		);
		const characters = await characterData.json();
		console.log(characters);
		setCharacters(characters);
	};

	return (
		<>
			<h3 className='page-title'>Characters</h3>
			{characters.map((character) => (
				<h4 key={character.char_id} style={characterNames}>
					<Link to={`/characters/${character.char_id}`}>
                    {character.name}
                    </Link>
				</h4>
			))}
		</>
	);
}

export default CharactersNav;
