import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function EpisodesNav() {
	useEffect(() => {
		fetchEpisodes();
	}, []);

	const [episodes, setEpisodes] = useState([]);

	const fetchEpisodes = async () => {
		const episodesData = await fetch(
			'https://www.breakingbadapi.com/api/episodes'
		);
		const episodes = await episodesData.json();
		console.log(episodes);
		setEpisodes(episodes);
	};

	return (
		<>
			<h3 className='page-title'>Characters</h3>
			{characters.map((character) => (
				<h5 key={character.char_id}>
					<Link className='names-list' to={`/characters/${character.char_id}`}>
						{character.name}
					</Link>
				</h5>
			))}
		</>
	);
}
